import React from 'react'
import styled from 'styled-components'

interface SmallbarProps{
    /**
     * bolded balck text, for first 5 boxes
     */
    boldedText:string;
    number: string;
    url:string;
}

const Container = styled.article`
    background: #d1fae5;
    box-shadow: 2px 2px 10px 5px rgba(0,0,0,0.1);
    border-radius: 12px;
    line-height:20px;
    flex-direction:row;
    display:felx;
`

const Heading = styled.h2`
    font-family : Inter;
    font-weight : 600;
    font-size: 18px;
    line-height: 22px;
`

const Image = styled.img`
    height :20px;
    object-fit:cover;
    width:20px;
    margin-top:10px;
`
export function Smallbar({boldedText,number,url}:SmallbarProps){
    return (
        <Container>
            <Image src="https://image.flaticon.com/icons/png/512/679/679821.png" />
            <Heading>{boldedText}</Heading>
            <Heading>{number}</Heading>
        </Container>
)
}
