import React from 'react'
import styled from 'styled-components'
import { PieChart, Pie, Sector, Cell, ResponsiveContainer } from 'recharts';


interface CardProps{
    /**
     * bolded balck text, for first 5 boxes
     */
    boldedText:string;
    greyText:string;
}

const Container = styled.article`
    background: #ffffff;
    box-shadow: 2px 2px 10px 5px rgba(0,0,0,0.1);
    border-radius: 5px
    `
const Layout = styled.div`
    display:flex;
    flex-direction:column;
    align-items: flex-start
    padding: 16px;
    font-size: 12px;
    font-weight : 400;
`

const ColoredLine = () => (
    <hr
        style={{
            color:'#ba92d1',
            backgroundColor: '#ba92d1',
            height: 2,
            borderColor : '#ba92d1',
            marginLeft: 6, 
            marginRight: 6
        }}
    />
);

const Heading = styled.h2`
    font-family : Inter;
    font-weight : 600;
    font-size: 25px;
    line-height: 40px;
`
const data = [
    { name: 'Others', value: 1975.45 },
    { name: 'B07MCGRV7M', value: 1815.67 },
    { name: 'B07X47P9GC', value: 595 },
    { name: 'B086JVLXW2', value: 189 },
    { name: 'B08R69L61K', value: 179 },
  ];
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042','#D9E1EC'];


export function Card({boldedText, greyText}:CardProps){
    return (
        <Container>
            <Heading>{boldedText}</Heading>

            <ColoredLine />
            <Layout>
                {greyText}
            </Layout>
            <PieChart width={800} height={400} >
        <Pie
          data={data}
          cx={120}
          cy={200}
          innerRadius={60}
          outerRadius={80}
          fill="#8884d8"
          paddingAngle={5}
          dataKey="value"
        >
          {data.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
          ))}
        </Pie>
        <Pie
          data={data}
          cx={420}
          cy={200}
          startAngle={180}
          endAngle={0}
          innerRadius={60}
          outerRadius={80}
          fill="#8884d8"
          paddingAngle={5}
          dataKey="value"
        >
          {data.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
          ))}
        </Pie>
      </PieChart>
        </Container>
)
}
